       int x, y;
   } Position;

   typedef struct {
       Position* body;
       int length;
       int direction;
   } Snake;

   Snake snake = {
       .body = malloc(sizeof(Position) * 2),
       .length = 2,
       .direction = 0 // 0: up, 1: right, 2: down, 3: left
