// Function to convert a string to lowercase
std::string toLower(const std::string& str) {
    std::string lowerStr = str;
    std::transform(lowerStr.begin(), lowerStr.end(), lowerStr.begin(),
                   [](unsigned char c) { return std::tolower(c); });
    return lowerStr;
}

// Function to count occurrences of a word in a line
int countWordInLine(const std::string& line, const std::string& word) {
    std::istringstream iss(line);
    std::string temp;
    int count = 0;
    std::string lowerWord = toLower(word);

    while (iss >> temp) {
        if (toLower(temp) == lowerWord) {
            count++;
        }
    }

    return count;
}

int main() {
    std::string fileName;
    std::string wordToCount;
    std::cout << "Enter the name of the file: ";
    std::cin >> fileName;
    std::cout << "Enter the word to count (case-insensitive): ";
    std::cin >> wordToCount;

    std::ifstream file(fileName);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << fileName << std::endl;
        return 1;
    }

    std::string line;
    int totalCount = 0;

    while (std::getline(file, line)) {
        totalCount += countWordInLine(line, wordToCount);
    }

    file.close();

    std::cout << "The word '" << wordToCount << "' occurs " << totalCount << " times in the file." << std::endl;

    return 0;
}
